mapper.select:
select st.*, c.name as clazzName
    from student st left join clazz c on st.clazz_id = c.id
    
        
            st.name like concat('%',#{name},'%')
        
        
            and st.degree = #{degree}
        
        
            and st.clazz_id = #{clazzId}
        
    
order by st.update_time desc
